import React, {useRef, useState, useEffect, useContext} from 'react';
import './App.css';
import './checkout.css'
import HeaderBar from './header';
import ScrollToTop from './scroll-to-top';
import { ShopContext } from './shop-context';
import { Link } from "react-router-dom";


const Delivery = (props) => {
    return(
        <div className="delivery-option">
            <h1>Delivery Information</h1>
            <div className="delivery-option-info">
                <div>
                    <h3>Address</h3>
                    <input></input>
                </div>
                <div>
                    <h3>Name</h3>
                    <input></input>
                </div>
            </div>
        </div>
    )
}

const Pickup = (props) => {
    function getCurrentDate(separator='/'){

        let newDate = new Date()
        let date = newDate.getDate()+7;
        let month = newDate.getMonth() + 1;
        let year = newDate.getFullYear();
        
        return `${year}${separator}${month<10?`0${month}`:`${month}`}${separator}${date<10?`0${date}`:`${date}`}`
    }

    return(
        <div className="pickup-option">
            <h1>Pickup Information</h1>
            <p>Pickup address is 299 Brambling Way. Order will be ready on {getCurrentDate()}</p>
        </div>
    )
}

export const Checkout = () => {
    
    const {cartItems} = useContext(ShopContext);
    const {TotalPrice} = useContext(ShopContext);
    const {deliveryState} = useContext(ShopContext);

    useEffect(() => {
    var d;
    if(deliveryState){
        d = <Delivery/>;
    }else{
        d = <Pickup/>;
    }
}, [deliveryState]);

    return(
        <div className='checkoutpage'>
            <HeaderBar/>
            <div className="checkoutpage-content">
                <div className="checkoutpage-content-left">
                    {d} 
                    <div style={{'margin-left':'5%'}}>
                        <h1>Billing</h1>
                        <p>E-Transfer raymondliusmailbox@gmail.com. I'm too lazy to set up the whole banking thing lol.</p>
                        <h4>Payment Email</h4>
                        <input></input>
                    </div>
                    
                </div>
                <div className="checkoutpage-content-right">
                </div>
            </div>
            
            <ScrollToTop/>
        </div>
    );
}

export default Checkout;
