import React, {useEffect, useRef} from 'react';
import './App.css';
import HeaderBar from './header';
import './fruitPage.css';
import {motion, useAnimation, useInView} from "framer-motion";
import {Product} from './home';

const FruitDango = (prod) => {
    return(
        <div className='prod-page'>  
            <div className='fruit-img-container'>
                <img src={prod.source} className='prod-cat-img'></img>
                <motion.div animate={{opacity:1}} initial={{opacity:0}} transition={{type:'tween', duration: 1}}>
                <h1 className='fruit-title'>Fruit x Dango</h1>
                </motion.div>
                <motion.div animate={{opacity:1}} initial={{opacity:0}} transition={{type:'tween', duration: 1, delay:0.5}}>
                <p className='fruit-thumb-description'>Fresh. Fruity. Fun.</p>
                </motion.div>
            </div>
        </div>
    );
}


const FruitPage = () => {
    const ref = useRef(null);
    const isInView = useInView(ref, {once:true});

    const mainControls = useAnimation();
    const slideControls = useAnimation();   

    useEffect(() => {
        if(isInView){
            mainControls.start('visible');
        }
    }, [isInView]);

    return(
        <div className='fruitpage' ref={ref}>
            <HeaderBar/>
            <FruitDango source='fruittumbnail.jpg'/>
            <motion.div
                variants={{
                    hidden:{opacity:0, y:75},
                    visible:{opacity:1, y:0},
                }}
                initial='hidden'
                animate={mainControls}
                transition={{duration:0.5, delay:0.25}}
            >
            </motion.div>
            
            <motion.div
                variants={{
                    hidden:{opacity:0, y:75},
                    visible:{opacity:1, y:0},
                }}
                initial='hidden'
                animate={mainControls}
                className='fruit-contents'
                transition={{duration:0.5, delay:0.25}}
            >
                <Product name={'Strawberry Dango'} description={'Tastes great'} image={'starwberrydango.jpg'} path={'/productPage1'}/>
                <Product name={'Orange Dango'} description={'Tastes great'} image={'orangedango.jpg'}/>
            </motion.div>
        </div>
    );
}

export default FruitPage;
