import React from 'react';
import './App.css';
import Slideshow from './slideshow';
import HeaderBar from './header';
import { Link } from "react-router-dom";
import { motion } from 'framer-motion';

const products = [''];

const Product = (prods) => {
    return(
      <motion.div whileHover={{ scale:1.05}}>
        <Link to={prods.path} style={{  'text-decoration': 'none'}}>
          <div className='product-container'>
            <img className = "product-image" src={prods.image}></img>
            <h1 style={{'font-size':'24px'}}>{prods.name}</h1>
            <p>{prods.description}</p>
          </div>
        </Link>
      </motion.div>
      
      
    );
}

const Category = (cats) => {
    return(
        <Link to={cats.path}>
          <div className='category-container'>
            <img className = "category-image" src={cats.image}></img>
          </div>
        </Link>
      );
}

function HomePage(){
    return(
    <div className='home'>
        <HeaderBar/>
        <Slideshow />
        <div className="contents">
            <div className='New'>
            <h2 style={{'text-align':'center', 'margin-top':'30px', 'font-size':'30px'}}>New Arrivals</h2>
            <div style={{'display': 'flex','justify-content':'center', 'gap':'40px','margin-left':'5%', 'margin-bottom':'20px', 'margin-right':'5%'}}>  
              <Product name={'Strawberry Dango'} description={'Tastes great'} image={'starwberrydango.jpg'} path={'/productPage1'}/>
              <Product name={'Orange Dango'} description={'Tastes great'} image={'orangedango.jpg'}/>
              <Product name={'Shiba Dango'} description={'Tastes great'} image={'shibaDango.jpg'}/>
            </div>
            </div>
            <div classname='Categories-section'>
                <h2 style={{'text-align':'center', 'font-size':'30px'}}>Categories</h2>
                <div className='category'>
                    <Category image='Fruit.png' path='/fruit'/>
                    <Category image='Candy.png'/>
                    <Category image='Classic.png'/>
                    <Category image='specialedition.png'/>
                </div>
            </div>
        </div>
    </div>
    );
}

export default HomePage;
