import React, {useRef, useEffect} from 'react';
import './App.css';
import Slideshow from './slideshow';
import HeaderBar from './header';
import { Link } from "react-router-dom";
import { motion, useAnimation} from 'framer-motion';
import { useInView } from "react-intersection-observer";

const products = [''];

export const Product = (prods) => {
    return(
      <motion.div whileHover={{ scale:1.1}}>
        <Link to={prods.path} style={{  'text-decoration': 'none'}}>
          <div className='product-container'>
            <img className = "product-image" src={prods.image}></img>
            <h1 style={{'font-size':'24px'}}>{prods.name}</h1>
            <p>{prods.description}</p>
          </div>
        </Link>
      </motion.div>
    );
}

const Category = (cats) => {
    
    return(
      <motion.div whileHover={{ scale:1.1}}>
        <Link to={cats.path}>
          <div className='category-container'>
            <img className = "category-image" src={cats.image}></img>
          </div>
        </Link>
      </motion.div>
      );
      
    
}

function HomePage(){
    const controls = useAnimation();
    const [ref, inView] = useInView();

    useEffect(() => {
        if(inView){
            controls.start('visible');
        }
    }, [controls, inView]);

    return(
    <div className='home' >
        <HeaderBar/>
        <Slideshow />
        <div className="contents">
          <div className='New'>
            
          </div>
            
            <motion.div 
                variants={{
                hidden:{opacity:0, x:-75},
                visible:{opacity:1, x:0},
                }}
                initial='hidden'
                animate={controls}
                ref={ref}
                transition={{duration:0.5, delay:0.25}}
                classname='Categories-section'
            >
                <h2 style={{'text-align':'center', 'font-size':'30px', 'margin-bottom':'40px'}}>Categories</h2>
                <div className='category'>
                    <Category image='Fruit.png' path='/fruit'/>
                    <Category image='Candy.png'/>
                    <Category image='Classic.png'/>
                    <Category image='specialedition.png'/>
                </div>
            </motion.div>
        </div>
    </div>
    );
}

export default HomePage;
