import React from 'react';
import './App.css';
import Slideshow from './slideshow';
import HeaderBar from './header';
import { Link } from "react-router-dom";

const products = [''];

const Product = (prods) => {
    return(
      <Link>
        <div className='product-container'>
          <img className = "product-image" src={prods.image}></img>
          <h1>{prods.name}</h1>
          <p>{prods.description}</p>
        </div>
      </Link>
      
    );
}

const Category = (cats) => {
    return(
        <Link>
          <div className='category-container'>
            <img className = "category-image" src={cats.image}></img>
          </div>
        </Link>
      );
}

function HomePage(){
    return(
    <div className='home'>
        <HeaderBar/>
        <Slideshow />
        <div className="contents">
            <div className='New'>
            <h2 style={{'margin-left':'5%'}}>New Arrivals</h2>
            <div style={{'display': 'flex','justify-content':'flex-start', 'gap':'20px','margin-left':'5%', 'margin-bottom':'20px', 'margin-right':'5%'}}>
                <Product name={'Strawberry Dango'} description={'Tastes great'} image={'starwberrydango.jpg'}/>
                <Product name={'Orange Dango'} description={'Tastes great'} image={'orangedango.jpg'}/>
                <Product name={'Shiba Dango'} description={'Tastes great'} image={'shibaDango.jpg'}/>
            </div>
            </div>
            <div classname='Categories-section'>
                <h2 style={{'margin-left':'5%'}}>Categories</h2>
                <div className='category'>
                    <Category image='Fruit.png'/>
                    <Category image='Candy.png'/>
                    <Category image='Classic.png'/>
                </div>
            </div>
        </div>
    </div>
    );
}

export default HomePage;
