import React, {useState} from 'react';
import './App.css';
import HeaderBar from './header';
import './ppage.css';
import ScrollToTop from './scroll-to-top';

const StrawberryPage = () => {

    const [style, setStyle] = useState({div1:false, div2:false});
    const [price, setPrice] = useState(5);
  
    function changePrice(NewPrice){
        setPrice(NewPrice);
        console.log(price);
    }

    function Selected(divName, NewPrice){
        console.log([divName]);
        return ()=>{
            if([divName] === "option-box2"){
                
                changePrice(5);
            }else{
                changePrice(NewPrice);
            }  
            handleDivClick('div1');
        }
        
    }

    const handleDivClick = (divName) => {
        setStyle((prevState) => ({
          ...prevState,
          [divName]: !prevState[divName],
        }));
      };
    

    return(
        <div className='productpage'>
            <HeaderBar/>
            <div className='product-page-container'>
                <div>
                    <img src='starwberrydango.jpg' className='ppage-img'></img>
                </div>
                <div  className='ppage-description'>
                    <h1 style={{'line-height':'0px'}}>Strawberry Dango</h1>
                    <p style={{'font-size':'20px'}}>Starting at $5</p>
                    <h3 style={{'padding-top':'50px'}}>Packaging</h3>
                    <div className='options'>
                        <div
                            className={`option-box ${style.div1 ? 'active' : ''}`}
                            onClick={() => Selected('div1',5)}
                            >
                                <p><b>Single</b></p>
                                <p>$5</p>
                        </div>
                        <div
                            className={`option-box ${style.div2 ? 'active' : ''}`}
                            onClick={() => handleDivClick('div2')}
                            >
                                <p><b>Set of 3</b></p>
                                <p>$12</p>
                        </div>

                        {/* <div className={style} onClick={Selected(12)}>
                            <p><b>Set of 3</b></p>
                            <p>$12</p>
                        </div> */}
                    </div>
                    <p>Total: ${price}</p>
                </div>
                
            </div>
            <ScrollToTop/>
        </div>
    );
}

export default StrawberryPage;
