import logo from './logo.svg';
import './App.css';
import React from 'react';


const Product = (prods) => {
  return(
    <>
    <img className = "image" src={prods.image}></img>
    <h1>{prods.name}</h1>
    <p>{prods.description}</p>
    </>
  )
}

function App() {
  return (
    <div className="App">
      <div className="header">
        <div className="nav-button">
          <input type="image" src="icons8-bars-50.png" className="nav-button"></input>
        </div>
        <div className="Title">
          <h1>DanGo!</h1>
        </div>
        <div className="cart-icon">
          <input type="image" src="icons8-cart-50.png" className="nav-button"></input>
        </div>  
      </div>
      <div className="featured">

      </div>
      <div className="contents">
        <Product name={'Strawberry Dango'} description={'Tastes great'} image={'starwberrydango.jpg'}/>
      </div>

    </div>
  );
}

const colors = ["#0088FE", "#00C49F", "#FFBB28"];
const delay = 2500;

function Slideshow() {
  const [index, setIndex] = React.useState(0);
  const timeoutRef = React.useRef(null);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  React.useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(
      () =>
        setIndex((prevIndex) =>
          prevIndex === colors.length - 1 ? 0 : prevIndex + 1
        ),
      delay
    );

    return () => {
      resetTimeout();
    };
  }, [index]);

  return (
    <div className="slideshow">
      <div
        className="slideshowSlider"
        style={{ transform: `translate3d(${-index * 100}%, 0, 0)` }}
      >
        {colors.map((backgroundColor, index) => (
          <div
            className="slide"
            key={index}
            style={{ backgroundColor }}
          ></div>
        ))}
      </div>

      <div className="slideshowDots">
        {colors.map((_, idx) => (
          <div
            key={idx}
            className={`slideshowDot${index === idx ? " active" : ""}`}
            onClick={() => {
              setIndex(idx);
            }}
          ></div>
        ))}
      </div>
    </div>
  );
}

export default App;
