import React, {useState} from 'react';
import './App.css';
import HeaderBar from './header';
import './ppage.css';
import ScrollToTop from './scroll-to-top';

const StrawberryPage = () => {

    const [style, setStyle] = useState(["option-box","optiion-box2"]);
    const [price, setPrice] = useState(20);
  
    function changePrice(NewPrice){
        setPrice(NewPrice);
        console.log(price);
    }

    function Selected(NewPrice){
        return ()=>{
            if(style === "option-box2"){
                setStyle("option-box");
                changePrice(5);
            }else{
                setStyle("option-box2");
                changePrice(NewPrice);
            }  
        }
        
    }

    const updateDivState = (index, newState) => {
        setStyle(prevStates => {
          const updatedStates = [...prevStates];
          updatedStates[index] = newState;
          return updatedStates;
        });
      };

    return(
        <div className='productpage'>
            <HeaderBar/>
            <div className='product-page-container'>
                <div>
                    <img src='starwberrydango.jpg' className='ppage-img'></img>
                </div>
                <div  className='ppage-description'>
                    <h1 style={{'line-height':'0px'}}>Strawberry Dango</h1>
                    <p style={{'font-size':'20px'}}>Starting at $5</p>
                    <h3 style={{'padding-top':'50px'}}>Packaging</h3>
                    <div className='options'>

                    {divStates.map((state, index) => (
                    <div
                    key={index}
                    className={`your-class ${state ? 'active' : ''}`}
                    onClick={() => updateDivState(index, !state)}
                    >
                    {/* Content of the div */}
                    </div>
                ))}

                        <div className={style} onClick={Selected(5)}>
                            <p><b>Single</b></p>
                            <p>$5</p>
                        </div>
                        <div className={style} onClick={Selected(12)}>
                            <p><b>Set of 3</b></p>
                            <p>$12</p>
                        </div>
                    </div>
                    <p>Total: ${price}</p>
                </div>
                
            </div>
            <ScrollToTop/>
        </div>
    );
}

export default StrawberryPage;
