import React, {useRef, useState, useEffect} from 'react';
import './App.css';
import HeaderBar from './header';
import './ppage.css';
import ScrollToTop from './scroll-to-top';

const StrawberryPage = () => {

    const [style, setStyle] = useState({div1:false, div2:false, div3:false, div4:false});
    const [price, setPrice] = useState(0);
    const [deliveryStyle, setDeliStyle] = useState({div1:false, div2:true});
    const [deliveryPrice, setDeliveryPrice] = useState(0);
    const [quantity, setQuantity] = useState(1);
    console.log("og",{quantity});
    

    let selectedPrice = useRef(null);

    
    function changePrice(NewPrice){
        console.log('hello' + quantity);
        setPrice(quantity*NewPrice + deliveryPrice);
    }

    function changeDeliveryFee(dprice){
        setPrice(price + dprice);
    }

    function ResetPrice(){
        setPrice(0);
    }

    function Selected(divName, NewPrice){
        
        if(style[divName] === true){
            handleDivClick(divName);
            selectedPrice.current = 0;
            changePrice(0);
            
        }else{
            selectedPrice.current = NewPrice;
            setStyle({div1: false, div2: false, div3:false, div4:false});
            ResetPrice();
            handleDivClick(divName);
            changePrice(NewPrice);
        } 
    
    }

    const handleDivClick = (divName) => {
        
        setStyle((prevState) => ({
          ...prevState,
          [divName]: !prevState[divName],
        }));
      };
    
    function DeliSelected(divName){
        if(deliveryStyle[divName] !== true || divName === 'div1'){
            if(deliveryStyle[divName] === true || divName === 'div2'){
                handleDeliveryClick(divName);
                setDeliveryPrice(0);
                changeDeliveryFee(-10);
                setDeliStyle({div2: true});
            }else{
                setDeliStyle({div1: false, div2: false});
                handleDeliveryClick(divName);
                setDeliveryPrice(10);
                changeDeliveryFee(10);
            }
        }
    
    }

    const handleDeliveryClick = (divName) => {
        
        setDeliStyle((prevState) => ({
          ...prevState,
          [divName]: !prevState[divName],
        }));
      };
    

    useEffect(() => {
    console.log(quantity);
    }, [quantity]);
  
    const DecreaseQuantity=()=>{
        
        if(quantity > 1){
            setQuantity(quantity-1);
            console.log(quantity);
            changePrice(selectedPrice.current);
        }
    }

    const IncreaseQuantity=()=>{
        if(style.div1 === true || style.div2 === true || style.div3 === true || style.div4 === true){
            setQuantity(quantity+1);
            console.log(quantity);
            changePrice(selectedPrice.current);
        }
    }

    return(
        <div className='productpage'>
            <HeaderBar/>
            <div className='product-page-container'>
                <div>
                    <img src='starwberrydango.jpg' className='ppage-img'></img>
                </div>
                <div  className='ppage-description'>
                    <h1 style={{'line-height':'0px'}}>Strawberry Dango</h1>
                    <p style={{'font-size':'20px'}}>Starting at $5</p>
                    <h3 style={{'padding-top':'50px'}}>Packaging</h3>
                    <div className='options'>
                        <div
                            className={`option-box ${style.div1 ? 'active' : ''}`}
                            onClick={() => Selected('div1',5)}
                            >
                                <p><b>Single</b></p>
                                <p>$5</p>
                        </div>
                        <div
                            className={`option-box ${style.div2 ? 'active' : ''}`}
                            onClick={() => Selected('div2',10)}
                            >
                                <p><b>Set of 3</b></p>
                                <p>$10</p>
                        </div>
                        <div
                            className={`option-box ${style.div3 ? 'active' : ''}`}
                            onClick={() => Selected('div3',12)}
                            >
                                <p><b>Set of 5</b></p>
                                <p>$12</p>
                        </div>
                        <div
                            className={`option-box ${style.div4 ? 'active' : ''}`}
                            onClick={() => Selected('div4',19)}
                            >
                                <p><b>Luxury Set</b></p>
                                <p>$19</p>
                        </div>
                    </div>
                    <div style={{'display':'grid', 'grid-template-columns':'50% 50%'}}>
                        <div style={{'display':'flex', 'flex-direction':'row','flex-wrap':'wrap','gap':'10px'}}>
                            <div
                                className={`delivery-box ${deliveryStyle.div1 ? 'active' : ''}`}
                                onClick={() => DeliSelected('div1',10)}
                                >
                                    <p><b>Delivery</b></p>
                                    <p>$10</p>
                            </div>
                            <div
                                className={`delivery-box ${deliveryStyle.div2 ? 'active' : ''}`}
                                onClick={() =>{DeliSelected('div2',0)}}
                                >
                                    <p><b>Pick up</b></p>
                                    <p>Free</p>
                            </div>
                        </div>
                        <div>
                            <p>Quantity</p>
                            <div style={{'display':'flex'}}>
                                <button onClick={() => {DecreaseQuantity()}}>-</button>
                                <p>{quantity}</p>
                                <button onClick={() => {IncreaseQuantity()}}>+</button>
                            </div>
                            <p style={{'text-align':'right', 'margin-right':'50%'}}>Total: ${price}</p>
                        </div>
                        
                    </div>
                    
                </div>
                
            </div>
            <ScrollToTop/>
        </div>
    );
}

export default StrawberryPage;
